// TikTok download function
async function downloadTikTok(url) {
  const input = {
    urls: [url],
    proxy: {
      useApifyProxy: true
    }
  }

  const run = await client.actor('apilabs/tiktok-downloader').call(input)
  const { items } = await client.dataset(run.defaultDatasetId).listItems()

  if (!items || items.length === 0) {
    throw new Error('No data found for TikTok video')
  }

  const item = items[0]
  return {
    platform: 'tiktok',
    title: item.text || 'TikTok Video',
    author: item.authorMeta?.nickName || item.authorMeta?.name || 'Unknown',
    thumbnail: item.covers?.[0] || item.dynamicCover || null,
    videoUrl: item.videoUrl || item.videoUrlNoWaterMark || null,
    duration: item.duration || null,
    views: item.stats?.playCount || null,
    likes: item.stats?.diggCount || null,
    shares: item.stats?.shareCount || null,
    comments: item.stats?.commentCount || null
  }
}

// YouTube download function
async function downloadYouTube(url) {
  const input = {
    videos: [{ url }]
  }

  const run = await client.actor('streamers/youtube-video-downloader').call(input)
  const { items } = await client.dataset(run.defaultDatasetId).listItems()

  if (!items || items.length === 0) {
    throw new Error('No data found for YouTube video')
  }

  const item = items[0]
  return {
    platform: 'youtube',
    title: item.title || 'YouTube Video',
    author: item.uploader || item.channel || 'Unknown Channel',
    thumbnail: item.thumbnail || null,
    videoUrl: item.url || null,
    duration: item.duration || null,
    views: item.view_count || null,
    description: item.description || null,
    uploadDate: item.upload_date || null,
    formats: item.formats?.map(format => ({
      quality: format.format_note || format.height ? `${format.height}p` : 'Unknown',
      ext: format.ext || 'mp4',
      url: format.url,
      filesize: format.filesize ? formatFileSize(format.filesize) : null
    })) || null
  }
}

// Instagram download function
async function downloadInstagram(url) {
  const input = {
    startUrls: [{ url }],
    proxy: {
      useApifyProxy: true
    },
    maxRequestRetries: 10
  }

  const run = await client.actor('pocesar/download-instagram-video').call(input)
  const { items } = await client.dataset(run.defaultDatasetId).listItems()

  if (!items || items.length === 0) {
    throw new Error('No data found for Instagram video')
  }

  const item = items[0]
  return {
    platform: 'instagram',
    title: item.caption || 'Instagram Video',
    author: item.ownerUsername || 'Unknown',
    thumbnail: item.displayUrl || item.thumbnailSrc || null,
    videoUrl: item.videoUrl || null,
    views: item.videoViewCount || null,
    likes: item.likesCount || null,
    comments: item.commentsCount || null,
    timestamp: item.timestamp || null
  }
}

// Utility function to format file size
function formatFileSize(bytes) {
  if (!bytes) return null
  const sizes = ['Bytes', 'KB', 'MB', 'GB']
  if (bytes === 0) return '0 Bytes'
  const i = Math.floor(Math.log(bytes) / Math.log(1024))
  return Math.round(bytes / Math.pow(1024, i) * 100) / 100 + ' ' + sizes[i]
}

